INSERT INTO `phpl_message` VALUES (1,'0ffd11a7-f7a6-4b4e-95af-0bd2e5af0168','Do you want to continue receiving our messages?','','','','<p>Hi [FIRST NAME%%there], remember us? You first signed up for our email newsletter on&nbsp;[ENTERED] &ndash; please click here to confirm you&#39;re happy to continue receiving our messages:</p>\n\n<p><strong><a href=\"[CONFIRMATIONURL]\" title=\"Confirm subscription\">Continue receiving messages</a></strong>&nbsp; <u>(If you do not confirm using this link, then you won&#39;t hear from us again</u>)</p>\n\n<p>While you&#39;re at it, you can also update your preferences, including your email address or other details, by clicking here:</p>\n\n<p><strong><a href=\"[PREFERENCESURL]\">Update preferences</a></strong></p>\n\n<p>By confirming your membership and keeping your details up to date, you&#39;re helping us to manage and protect your data in accordance with best practices.</p>\n\n<p>Thank you!</p>',NULL,'--\n\n    <div class=\"footer\" style=\"text-align:left; font-size: 75%;\">\n      <p>This message was sent to [EMAIL] by [FROMEMAIL].</p>\n      <p>To forward this message, please do not use the forward button of your email application, because this message was made specifically for you only. Instead use the <a href=\"[FORWARDURL]\">forward page</a> in our newsletter system.<br/>\n      To change your details and to choose which lists to be subscribed to, visit your personal <a href=\"[PREFERENCESURL]\">preferences page</a>.<br/>\n      Or you can <a href=\"[UNSUBSCRIBEURL]\">opt-out completely</a> from all future mailings.</p>\n    </div>\n\n  ','2020-02-09 19:28:02','2020-02-09 18:28:02',NULL,0,NULL,0,NULL,'draft',NULL,NULL,0,'invite',NULL,0,0,0,0,0,0,0,0,NULL,NULL,1);
